import React, { createContext, useState } from 'react';
import { signIn } from '../services/api';

const AuthContext = createContext({ signed: Boolean, tipo_usuario: String, token: String, user: Object, AuthLogin: Function, SignOut: Function});

export function AuthProvider({children}){
    const [user, setUser] = useState({});
    const [token, setToken] = useState();
    const [tipousuario, setTipoUsuario] = useState(null);

    async function AuthLogin(email, password){
        const {token, nome, tipo_usuario} = await signIn();
        setToken(token);
        setUser(nome);
        setTipoUsuario(tipo_usuario);
        //const response = await signIn();
        //console.log(response);
    }

    function SignOut(){
        setToken(null);
        setUser({});
    }

    return (
        <AuthContext.Provider value={{ signed: !!token, token, tipo_usuario, user, AuthLogin, SignOut}} >
            {children}
        </AuthContext.Provider>
    );
}

export default AuthContext;
