import React, { createContext, useState } from 'react';
import { signIn } from '../services/api';

const AuthContext = createContext({ signed: Boolean, setTipo_usuario: String, token: String, user: Object, AuthLogin: Function, SignOut: Function});

export function AuthProvider({children}){
    const [user, setUser] = useState({});
    const [token, setToken] = useState(); 
    const [tipo_usuario, setTipo_usuario] = useState(); 

    async function AuthLogin(email, password){
        //const {token, user} = await signIn();
        setToken(email);
        setUser(password);
        setTipo_usuario('paciente');

    }

    function SignOut(){
        setToken(null);
        setUser({});
    }

    return (
        <AuthContext.Provider value={{ signed: !!token, setTipo_usuario, token, user, AuthLogin, SignOut}} >
            {children}
        </AuthContext.Provider>
    );
}

export default AuthContext;
