import React, { createContext, useStates } from 'react';
//import { signIn } from '../services/api';

const AuthContext = createContext({ signed: Boolean, token: String, user: Object, AuthLogin: Function, SignOut: Function});

export function AuthProvider({children}){
    const [user, setUser] = useState({});
    const [token, setToken] = useState(null);
    //const [tipousuario, setTipoUsuario] = useState(null);

    function AuthLogin(email, password){
        //const response = await signIn();
        //console.log(response);
    }

    function SignOut(){
        setToken(null);
        setUser({});
    }

    return (
        <AuthContext.Provider value={{ signed: !!token, token, user, loading, AuthLogin, SignOut}} >
            {children}
        </AuthContext.Provider>
    );
}

export default AuthContext;
